import os, discord, sqlite3, asyncio, logging, threading, datetime, json, re, base64, sys
from discord.ext import commands; from discord import app_commands, ui
from dotenv import load_dotenv

load_dotenv(); TOKEN = os.getenv('DISCORD_TOKEN'); db_path = 'bot_data.db'

def db_exec(q, p=()):
    try:
        with sqlite3.connect(db_path) as conn: return conn.execute(q, p).fetchall()
    except: return []
def db_commit(q, p=()):
    try:
        with sqlite3.connect(db_path) as conn: conn.execute(q, p); conn.commit()
    except: pass

def init_db():
    db_commit('CREATE TABLE IF NOT EXISTS active_feats (gid INTEGER, fid INTEGER, PRIMARY KEY(gid, fid))')
    db_commit('CREATE TABLE IF NOT EXISTS logs (id INTEGER PRIMARY KEY, gid INTEGER, uid INTEGER, msg TEXT, time TIMESTAMP DEFAULT CURRENT_TIMESTAMP)')
init_db()

class HelpView(ui.View):
    def __init__(self, pages):
        super().__init__(timeout=120)
        self.pages = pages; self.current_page = 0
    @ui.button(label='Back', style=discord.ButtonStyle.blurple)
    async def prev(self, itx: discord.Interaction, btn: ui.Button):
        if not self.pages: return
        self.current_page = (self.current_page - 1) % len(self.pages)
        await itx.response.edit_message(embed=self.pages[self.current_page], view=self)
    @ui.button(label='Next', style=discord.ButtonStyle.blurple)
    async def next(self, itx: discord.Interaction, btn: ui.Button):
        if not self.pages: return
        self.current_page = (self.current_page + 1) % len(self.pages)
        await itx.response.edit_message(embed=self.pages[self.current_page], view=self)

